&lt;span class=&#34;keyword&#34;&gt;const&lt;/span&gt; removeLocalStorage = &lt;span class=&#34;function&#34;&gt;(&lt;span class=&#34;params&#34;&gt;key&lt;/span&gt;) =&amp;gt;&lt;/span&gt; &amp;#123;
    &lt;span class=&#34;keyword&#34;&gt;try&lt;/span&gt; &amp;#123;
        &lt;span class=&#34;built_in&#34;&gt;localStorage&lt;/span&gt;.removeItem(key);
    &amp;#125; &lt;span class=&#34;keyword&#34;&gt;catch&lt;/span&gt; (e) &amp;#123;&amp;#125;
&amp;#125;

&lt;span class=&#34;keyword&#34;&gt;const&lt;/span&gt; getLocalStorage = &lt;span class=&#34;function&#34;&gt;(&lt;span class=&#34;params&#34;&gt;key&lt;/span&gt;) =&amp;gt;&lt;/span&gt; &amp;#123;
    &lt;span class=&#34;keyword&#34;&gt;try&lt;/span&gt; &amp;#123;
        &lt;span class=&#34;keyword&#34;&gt;return&lt;/span&gt; &lt;span class=&#34;built_in&#34;&gt;localStorage&lt;/span&gt;.getItem(key);
    &amp;#125; &lt;span class=&#34;keyword&#34;&gt;catch&lt;/span&gt; (e) &amp;#123;
        &lt;span class=&#34;keyword&#34;&gt;return&lt;/span&gt; &lt;span class=&#34;literal&#34;&gt;null&lt;/span&gt;;    &lt;span class=&#34;comment&#34;&gt;// 与 localStorage 中没有找到对应 key 的行为一致&lt;/span&gt;
    &amp;#125;
&amp;#125;&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;获取当前 &lt;code&gt;prefers-color-scheme&lt;/code&gt; 的方法：&lt;/p&gt;
&lt;pre&gt;&lt;code class=&#34;highlight javascript&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;const&lt;/span&gt; getModeFromCSSMediaQuery = &lt;span class=&#34;function&#34;&gt;() =&amp;gt;&lt;/span&gt; &amp;#123;
    &lt;span class=&#34;keyword&#34;&gt;return&lt;/span&gt; &lt;span class=&#34;built_in&#34;&gt;window&lt;/span&gt;.matchMedia(&lt;span class=&#34;string&#34;&gt;&amp;#x27;(prefers-color-scheme: dark)&amp;#x27;&lt;/span&gt;).matches ? &lt;span class=&#34;string&#34;&gt;&amp;#x27;dark&amp;#x27;&lt;/span&gt; : &lt;span class=&#34;string&#34;&gt;&amp;#x27;light&amp;#x27;&lt;/span&gt;;
&amp;#125;&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;再写一个清除 Class 和 LocalStorage 的函数：&lt;/p&gt;
&lt;pre&gt;&lt;code class=&#34;highlight javascript&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;const&lt;/span&gt; resetRootDarkModeClassAndLocalStorage = &lt;span class=&#34;function&#34;&gt;() =&amp;gt;&lt;/span&gt; &amp;#123;
