        currentSetting = invertDarkModeObj[currentSetting];
    &amp;#125; &lt;span class=&#34;keyword&#34;&gt;else&lt;/span&gt; &lt;span class=&#34;keyword&#34;&gt;if&lt;/span&gt; (currentSetting === &lt;span class=&#34;literal&#34;&gt;null&lt;/span&gt;) &amp;#123;
