&lt;span class=&#34;variable&#34;&gt;$reg&lt;/span&gt; = preg_match(&lt;span class=&#34;string&#34;&gt;&amp;#x27;/\d/is&amp;#x27;&lt;/span&gt;, &lt;span class=&#34;variable&#34;&gt;$_GET&lt;/span&gt;[&lt;span class=&#34;string&#34;&gt;&amp;#x27;id&amp;#x27;&lt;/span&gt;]);

