    &lt;span class=&#34;variable&#34;&gt;$content&lt;/span&gt; = file_get_contents(&lt;span class=&#34;variable&#34;&gt;$_GET&lt;/span&gt;[&lt;span class=&#34;string&#34;&gt;&amp;#x27;url&amp;#x27;&lt;/span&gt;]);

    &lt;span class=&#34;comment&#34;&gt;//文件路径猜解&lt;/span&gt;
    &lt;span class=&#34;keyword&#34;&gt;if&lt;/span&gt; (&lt;span class=&#34;literal&#34;&gt;false&lt;/span&gt;)&amp;#123;
        &lt;span class=&#34;keyword&#34;&gt;echo&lt;/span&gt; &lt;span class=&#34;string&#34;&gt;&amp;quot;还差一点点啦～&amp;quot;&lt;/span&gt;;
    &amp;#125;&lt;span class=&#34;keyword&#34;&gt;else&lt;/span&gt;&amp;#123;
        &lt;span class=&#34;keyword&#34;&gt;echo&lt;/span&gt; &lt;span class=&#34;variable&#34;&gt;$flag&lt;/span&gt;;
    &amp;#125;
&amp;#125;&lt;span class=&#34;keyword&#34;&gt;else&lt;/span&gt;&amp;#123;
    &lt;span class=&#34;keyword&#34;&gt;echo&lt;/span&gt; &lt;span class=&#34;string&#34;&gt;&amp;quot;你想要的不在这儿～&amp;quot;&lt;/span&gt;;
&amp;#125;
&lt;span class=&#34;meta&#34;&gt;?&amp;gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;然后需要构造出一个 符合 &lt;code&gt;!is_numeric($_GET[&#39;id&#39;]) and preg_match(&#39;/\d/is&#39;, $_GET[&#39;id&#39;]) !== 1 and intval($_GET[&#39;id&#39;]) === 1&lt;/code&gt; 的请求即可。&lt;/p&gt;
