 * @param r 右端点
 */
long long query(int u, int l, int r) &#123;
    if (tr[u].l &gt;= l &amp;&amp; tr[u].r &lt;= r) &#123;  // 被包含直接返回
        return tr[u].s;
    &#125;
    int mid = tr[u].l + tr[u].r &gt;&gt; 1;
    long long s = 0;
    pushdown(u);                                // 下放懒标记
    if (l &lt;= mid) s += query(u &lt;&lt; 1, l, r);     // 和左侧有交集
    if (r &gt; mid) s += query(u &lt;&lt; 1 | 1, l, r);  // 和右侧有交集
    return s;
&#125;

int main() &#123;
    cin &gt;&gt; n &gt;&gt; m;
    for (int i = 1; i &lt;= n; i++) &#123;
        cin &gt;&gt; a[i];
    &#125;
    build(1, 1, n);
    for (int i = 0; i &lt; m; i++) &#123;
        cin &gt;&gt; op &gt;&gt; x &gt;&gt; y;
        if (op == 1) &#123;
            cin &gt;&gt; k;
            modify(1, x, y, k);
        &#125;
        else if (op == 2) &#123;
            cout &lt;&lt; query(1, x, y) &lt;&lt; endl;
        &#125;
    &#125;
    return 0;
&#125;
区间加、乘
#include &lt;bits/stdc++.h&gt;

using namespace std;

struct node &#123;
    int l, r;
    long long s, d, x;

    node() &#123;
        l = r = s = d = 0;
        x = 1;
    &#125;
    node(int _l, int _r) &#123;
        l = _l, r = _r;
        s = d = 0;
        x = 1;
    &#125;
&#125; tr[100005 &lt;&lt; 2];
int n, m, p, op, x, y;
long long k, a[100005];

/**
 * 上传信息
 * @param u 父节点下标
 */
void pushup(int u) &#123;
    tr[u].s = (tr[u &lt;&lt; 1].s + tr[u &lt;&lt; 1 | 1].s) % p;
&#125;

/**
 * 下放懒标记
 * @param u 父节点下标
 * @attention 先乘后加
 */
void pushdown(int u) &#123;
    // 左子树
    tr[u &lt;&lt; 1].s = ((tr[u &lt;&lt; 1].s * tr[u].x) + (tr[u &lt;&lt; 1].r - tr[u &lt;&lt; 1].l + 1) * tr[u].d) % p;
    tr[u &lt;&lt; 1].x = tr[u &lt;&lt; 1].x * tr[u].x % p;
    tr[u &lt;&lt; 1].d = (tr[u &lt;&lt; 1].d * tr[u].x + tr[u].d) % p;
    // 右子树
    tr[u &lt;&lt; 1 | 1].s = ((tr[u &lt;&lt; 1 | 1].s * tr[u].x) + (tr[u &lt;&lt; 1 | 1].r - tr[u &lt;&lt; 1 | 1].l + 1) * tr[u].d) % p;
    tr[u &lt;&lt; 1 | 1].x = tr[u &lt;&lt; 1 | 1].x * tr[u].x % p;
    tr[u &lt;&lt; 1 | 1].d = (tr[u &lt;&lt; 1 | 1].d * tr[u].x + tr[u].d) % p;
    // 清除懒标记
    tr[u].d = 0;
    tr[u].x = 1;
&#125;

/**
 * 建立线段树
 * @param u 根节点下标
 * @param l 左端点
 * @param r 右端点
 */
void build(int u, int l, int r) &#123;
    tr[u] = node(l, r);
    if (l == r) &#123;
        tr[u].s = a[l] % p;
        return;
    &#125;
    int mid = l + r &gt;&gt; 1;
    build(u &lt;&lt; 1, l, mid);
    build(u &lt;&lt; 1 | 1, mid + 1, r);
    pushup(u);
&#125;

/**
 * 区间修改
 * @details 将区间 [l, r] 中的每一个数加上 d
 * @param u 父节点下标
 * @param l 左端点
 * @param r 右端点
 * @param x 乘上的数
 * @param d 增加的值
 */
void modify(int u, int l, int r, long long x, long long d) &#123;
    // 被包含直接修改
    if (tr[u].l &gt;= l &amp;&amp; tr[u].r &lt;= r) &#123;
        tr[u].s = ((tr[u].s * x) + (tr[u].r - tr[u].l + 1) * d) % p;
        tr[u].x = tr[u].x * x % p;
        tr[u].d = (tr[u].d * x + d) % p;
        return;
    &#125;
    int mid = tr[u].l + tr[u].r &gt;&gt; 1;
    pushdown(u);                                  // 下放懒标记
    if (l &lt;= mid) modify(u &lt;&lt; 1, l, r, x, d);     // 和左侧有交集
    if (r &gt; mid) modify(u &lt;&lt; 1 | 1, l, r, x, d);  // 和右侧有交集
    pushup(u);                                    // 上传新信息
&#125;

/**
 * 区间查询
 * @param u 
 * @param l 
 * @param r 
 * @return int 
 */
long long query(int u, int l, int r) &#123;
    if (tr[u].l &gt;= l &amp;&amp; tr[u].r &lt;= r) &#123;  // 被包含直接返回
        return tr[u].s;
    &#125;
    int mid = tr[u].l + tr[u].r &gt;&gt; 1;
    long long s = 0;
    pushdown(u);                                         // 下放懒标记
    if (l &lt;= mid) s = query(u &lt;&lt; 1, l, r);               // 和左侧有交集
    if (r &gt; mid) s = (s + query(u &lt;&lt; 1 | 1, l, r)) % p;  // 和右侧有交集
    return s;
&#125;

int main() &#123;
    cin &gt;&gt; n &gt;&gt; m &gt;&gt; p;
    for (int i = 1; i &lt;= n; i++) &#123;
        cin &gt;&gt; a[i];
    &#125;
    build(1, 1, n);
    while (m--) &#123;
        cin &gt;&gt; op &gt;&gt; x &gt;&gt; y;
        if (op == 1) &#123;
            cin &gt;&gt; k;
            modify(1, x, y, k, 0);
        &#125;
        else if (op == 2) &#123;
            cin &gt;&gt; k;
            modify(1, x, y, 1, k);
        &#125;
        else if (op == 3) &#123;
            cout &lt;&lt; query(1, x, y) % p &lt;&lt; endl;
        &#125;
    &#125;
    return 0;
&#125;

题面
难度：普及-
标签：模拟 字符串
题目描述
在幻想乡，东风谷早苗是以高达控闻名的高中生宅巫女。某一天，早苗终于入手了最新款的钢达姆模型。作为最新的钢达姆，当然有了与以往不同的功能了，那就是它能够自动行走。
早苗的新模型可以按照输入的命令进行移动，命令包含 E S W N 四种，分别对应四个不同的方向，依次为东、南、西、北。执行某个命令时，它会向着对应方向移动一个单位。作为新型机器人，模型自然不会只单单执行一个命令，它可以执行命令串。对于输入的命令串，每一秒它会按照命令行动一次。而执行完命令串最后一个命令后，会自动从头开始循环。在 000 秒时早苗将钢达姆放置在了 (0,0)(0,0)(0,0) 的位置，并且输入了命令串。

向东移动，坐标改变改变为 (x+1, y)(x+1,\ y)(x+1, y)
向南移动，坐标改变改变为 (x, y−1)(x,\ y-1)(x, y−1)
向西移动，坐标改变改变为 (x−1, y)(x-1,\ y)(x−1, y)
向北移动，坐标改变改变为 (x, y+1)(x,\ y+1)(x, y+1)

现在她想要知道 TTT 秒后钢达姆所在的位置坐标。
输入格式
第1行：一个字符串，表示早苗输入的命令串，保证至少有 1 个命令。
第2行：一个正整数 TTT 。
输出格式
第1行：两个整数，表示 TTT 秒时，钢达姆的坐标。
输入输出样例
输入 #1
NSWWNSNEEWN
12
输出 #1
-1 3
思路
60分
按照题意模拟即可
100分
由于题目数据范围过大，单纯模拟无法通过此题，所以需要寻找一些规律。

而执行完命令串最后一个命令后，会自动从头开始循环。

通过仔细读题可以发现，在运行中命令串可能会被执行多次，因此可以先算出命令串被执行了几次，再模拟出最终结果。
代码
60分
纯暴力做法
#include &lt;bits/stdc++.h&gt;

using namespace std;

int main() &#123;
    int    x = 0, y = 0, t = 0, i = 0;
    string s;
    cin &gt;&gt; s &gt;&gt; t;
    while (t--) &#123;
        if (i &gt;= s.size()) &#123;
            i = 0;
        &#125;
        switch (s[i]) &#123;
            case &#x27;E&#x27;: x++; break;
            case &#x27;S&#x27;: y--; break;
            case &#x27;W&#x27;: x--; break;
            case &#x27;N&#x27;: y++; break;
        &#125;
        i++;
    &#125;
    cout &lt;&lt; x &lt;&lt; &#x27; &#x27; &lt;&lt; y &lt;&lt; endl;
    return 0;
&#125;
