using namespace std;

int main() &#123;
    int n, l;
    string s;
    map&lt;string, set&lt;int&gt;&gt; m;
    cin &gt;&gt; n;
    for (int i = 1; i &lt;= n; i++) &#123;
        cin &gt;&gt; l;
        for (int j = 0; j &lt; l; j++) &#123;
            cin &gt;&gt; s;
            m[s].insert(i);
        &#125;
    &#125;
    cin &gt;&gt; n;
    for (int i = 0; i &lt; n; i++) &#123;
        cin &gt;&gt; s;
        if (!m.count(s)) &#123;
            cout &lt;&lt; endl;
        &#125;
        else &#123;
            for (set&lt;int&gt;::iterator it = m[s].begin(); it != --m[s].end(); it++) &#123;
                cout &lt;&lt; *it &lt;&lt; &#x27; &#x27;;
            &#125;
            cout &lt;&lt; *--m[s].end() &lt;&lt; endl;
        &#125;
    &#125;
    return 0;
