        &#125;
        else &#123;
            long long g = f[n]/n;
            for (long long i = n; i &gt; 0; i--) &#123;
                ans += abs(i * g - f[i]);
            &#125;
            cout &lt;&lt; ans &lt;&lt; endl;
        &#125;
    &#125;
