&lt;span class=&#34;hljs-built_in&#34;&gt;pair&lt;/span&gt;&amp;lt;__int128, __int128&amp;gt; ans[&lt;span class=&#34;hljs-number&#34;&gt;100005&lt;/span&gt;];

