    &lt;span class=&#34;hljs-keyword&#34;&gt;while&lt;/span&gt; (__n) &amp;#123;
        __o.push_back(__n%&lt;span class=&#34;hljs-number&#34;&gt;10&lt;/span&gt;+&lt;span class=&#34;hljs-string&#34;&gt;&amp;#x27;0&amp;#x27;&lt;/span&gt;);
        __n /= &lt;span class=&#34;hljs-number&#34;&gt;10&lt;/span&gt;;
    &amp;#125;
